import { useState } from "react";

const useInputField = (validateValue) => {

const [enteredValue, setEnteredValue] = useState('');
const [fieldIsTouched, setFieldIsTouched] = useState(false)

const valueValid = validateValue(enteredValue);
const hasError = !valueValid && fieldIsTouched;

const ValueChangeHandler = (event) => {
    setEnteredValue(event.target.value);
};

const inputFieldBlurHandler = (event) => {
    fieldIsTouched(true);
};

const reset = () => {
    setEnteredValue('');
    setFieldIsTouched(false);
};

return {
    value: enteredValue,
    isValid: valueValid,
    hasError,
    ValueChangeHandler,
    inputFieldBlurHandler,
    reset
};

};




export default useInputField;
